import React from 'react';
import { TouchableOpacity, Text, StyleSheet, ViewStyle, TextStyle, TouchableOpacityProps, ActivityIndicator, Animated, Platform, View } from 'react-native';
import { colors } from '@/constants/colors';
import { Gradient } from '@/components/ui/Gradient';

interface ButtonProps extends TouchableOpacityProps {
  title: string;
  variant?: 'primary' | 'secondary' | 'outline' | 'ghost' | 'gradient';
  size?: 'small' | 'medium' | 'large';
  isLoading?: boolean;
  disabled?: boolean;
  style?: ViewStyle;
  textStyle?: TextStyle;
  icon?: React.ReactNode;
  iconPosition?: 'left' | 'right';
  gradientVariant?: 'primary' | 'secondary' | 'accent' | 'success' | 'info';
}

export function Button({
  title,
  variant = 'primary',
  size = 'medium',
  isLoading = false,
  disabled = false,
  style,
  textStyle,
  icon,
  iconPosition = 'left',
  gradientVariant = 'primary',
  ...props
}: ButtonProps) {
  const animatedScale = new Animated.Value(1);

  const handlePressIn = () => {
    Animated.spring(animatedScale, {
      toValue: 0.95,
      useNativeDriver: true,
    }).start();
  };

  const handlePressOut = () => {
    Animated.spring(animatedScale, {
      toValue: 1,
      useNativeDriver: true,
    }).start();
  };

  const baseStyle: ViewStyle = {
    opacity: disabled ? 0.5 : 1,
    borderRadius: 8,
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'row',
    paddingVertical: size === 'small' ? 8 : size === 'medium' ? 12 : 16,
    paddingHorizontal: size === 'small' ? 16 : size === 'medium' ? 24 : 32,
  };

  const buttonStyles = StyleSheet.create({
    primary: {
      backgroundColor: colors.primary,
    },
    secondary: {
      backgroundColor: colors.secondary,
    },
    outline: {
      backgroundColor: 'transparent',
      borderWidth: 1,
      borderColor: colors.primary,
    },
    ghost: {
      backgroundColor: 'transparent',
    },
    gradient: {
      backgroundColor: 'transparent',
    },
  });

  const textStyles = StyleSheet.create({
    base: {
      color: '#FFFFFF',
      fontSize: size === 'small' ? 14 : size === 'medium' ? 16 : 18,
      fontWeight: '600',
      marginLeft: icon && iconPosition === 'left' ? 8 : 0,
      marginRight: icon && iconPosition === 'right' ? 8 : 0,
    },
    outline: {
      color: colors.primary,
    },
    ghost: {
      color: colors.primary,
    },
  });

  const renderContent = () => {
    const content = (
      <>
        {isLoading ? (
          <ActivityIndicator color={variant === 'outline' || variant === 'ghost' ? colors.primary : '#FFFFFF'} />
        ) : (
          <>
            {icon && iconPosition === 'left' && icon}
            <Text style={[textStyles.base, variant === 'outline' && textStyles.outline, variant === 'ghost' && textStyles.ghost, textStyle]}>
              {title}
            </Text>
            {icon && iconPosition === 'right' && icon}
          </>
        )}
      </>
    );

    return content;
  };

  const animatedStyle = {
    transform: [{ scale: animatedScale }],
  };

  if (variant === 'gradient') {
    return (
      <Animated.View style={animatedStyle}>
        <TouchableOpacity
          disabled={disabled || isLoading}
          style={[baseStyle, style]}
          activeOpacity={0.8}
          onPressIn={handlePressIn}
          onPressOut={handlePressOut}
          {...props}
        >
          <Gradient variant={gradientVariant} style={{ borderRadius: 8 }}>
            <View style={{ padding: baseStyle.paddingVertical }}>
              {renderContent()}
            </View>
          </Gradient>
        </TouchableOpacity>
      </Animated.View>
    );
  }

  return (
    <Animated.View style={animatedStyle}>
      <TouchableOpacity
        disabled={disabled || isLoading}
        style={[baseStyle, buttonStyles[variant], style]}
        activeOpacity={0.8}
        onPressIn={handlePressIn}
        onPressOut={handlePressOut}
        {...props}
      >
        {renderContent()}
      </TouchableOpacity>
    </Animated.View>
  );
}
