import React, { useEffect } from 'react';
import { View, Text, StyleSheet, ScrollView, FlatList, RefreshControl } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { useRouter } from 'expo-router';
import { TrendingUp, BookOpen } from 'lucide-react-native';
import { colors } from '@/constants/colors';
import { NoteCard } from '@/components/NoteCard';
import { CategoryCard } from '@/components/CategoryCard';
import { EmptyState } from '@/components/EmptyState';
import { useAuthStore } from '@/store/auth-store';
import { useNotesStore } from '@/store/notes-store';
import { mockCategories } from '@/mocks/categories';
import { FadeIn } from '@/components/animations/FadeIn';
import { SlideIn } from '@/components/animations/SlideIn';
import { Stagger } from '@/components/animations/Stagger';
import { Gradient } from '@/components/ui/Gradient';

export default function HomeScreen() {
  const router = useRouter();
  const { user, isAuthenticated } = useAuthStore();
  const { 
    trendingNotes, 
    recommendedNotes, 
    fetchTrendingNotes, 
    fetchRecommendedNotes,
    isLoading 
  } = useNotesStore();

  const [refreshing, setRefreshing] = React.useState(false);

  useEffect(() => {
    // Only load data if authenticated, but don't navigate away
    if (isAuthenticated) {
      loadData();
    }
  }, [isAuthenticated]);

  const loadData = async () => {
    await fetchTrendingNotes();
    if (user?.interests) {
      await fetchRecommendedNotes(user.interests);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadData();
    setRefreshing(false);
  };

  // If not authenticated, show login prompt instead of redirecting
  if (!isAuthenticated) {
    return (
      <SafeAreaView style={styles.container} edges={['bottom']}>
        <FadeIn duration={600}>
          <View style={styles.authPromptContainer}>
            <Gradient 
              style={styles.welcomeGradient}
              colors={[colors.primary, colors.accent]}
              start={{ x: 0, y: 0 }}
              end={{ x: 1, y: 1 }}
            >
              <Text style={styles.welcomeTitle}>StudySphere</Text>
              <Text style={styles.welcomeSubtitle}>Your study notes marketplace</Text>
            </Gradient>

            <Text style={styles.authPromptTitle}>Welcome to StudySphere</Text>
            <Text style={styles.authPromptText}>
              Please log in or create an account to access study notes and more.
            </Text>
            <View style={styles.authButtonsContainer}>
              <EmptyState
                title="Get Started"
                description="Log in to access your personalized study notes"
                actionLabel="Log In"
                onAction={() => router.push('/(auth)/login')}
              />
            </View>
          </View>
        </FadeIn>
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={styles.container} edges={['bottom']}>
      <ScrollView
        showsVerticalScrollIndicator={false}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        <SlideIn direction="top" duration={600}>
          <View style={styles.header}>
            <Text style={styles.greeting}>Hello, {user?.name?.split(' ')[0] || 'Student'}</Text>
            <Text style={styles.subGreeting}>Find the perfect study notes</Text>
          </View>
        </SlideIn>

        <SlideIn direction="right" duration={600} delay={200}>
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Browse by Subject</Text>
            <FlatList
              data={mockCategories}
              renderItem={({ item, index }) => (
                <CategoryCard 
                  category={item} 
                  delay={index * 100}
                />
              )}
              keyExtractor={(item) => item.id}
              horizontal
              showsHorizontalScrollIndicator={false}
              contentContainerStyle={styles.categoriesContainer}
            />
          </View>
        </SlideIn>

        <SlideIn direction="left" duration={600} delay={400}>
          <View style={styles.section}>
            <View style={styles.sectionHeader}>
              <View style={styles.iconWrapper}>
                <TrendingUp size={20} color={colors.primary} />
              </View>
              <Text style={styles.sectionTitle}>Trending Notes</Text>
            </View>

            {isLoading && trendingNotes.length === 0 ? (
              <View style={styles.loadingContainer}>
                <Text style={styles.loadingText}>Loading trending notes...</Text>
              </View>
            ) : trendingNotes.length === 0 ? (
              <EmptyState
                title="No trending notes yet"
                description="Be the first to upload study notes and start a trend!"
                icon={<View style={styles.iconWrapper}><TrendingUp size={40} color={colors.textTertiary} /></View>}
                actionLabel="Upload Notes"
                onAction={() => router.push('/upload')}
              />
            ) : (
              <Stagger staggerDelay={150}>
                {trendingNotes.slice(0, 3).map((note, index) => (
                  <NoteCard key={note.id} note={note} delay={index * 100} />
                ))}
              </Stagger>
            )}
          </View>
        </SlideIn>

        <SlideIn direction="right" duration={600} delay={600}>
          <View style={styles.section}>
            <View style={styles.sectionHeader}>
              <View style={styles.iconWrapper}>
                <BookOpen size={20} color={colors.secondary} />
              </View>
              <Text style={styles.sectionTitle}>Recommended for You</Text>
            </View>

            {isLoading && recommendedNotes.length === 0 ? (
              <View style={styles.loadingContainer}>
                <Text style={styles.loadingText}>Loading recommendations...</Text>
              </View>
            ) : recommendedNotes.length === 0 ? (
              <EmptyState
                title="No recommendations yet"
                description="Update your interests in profile to get personalized recommendations"
                icon={<View style={styles.iconWrapper}><BookOpen size={40} color={colors.textTertiary} /></View>}
                actionLabel="Update Interests"
                onAction={() => router.push('/profile')}
              />
            ) : (
              <Stagger staggerDelay={150}>
                {recommendedNotes.slice(0, 4).map((note, index) => (
                  <NoteCard key={note.id} note={note} delay={index * 100} />
                ))}
              </Stagger>
            )}
          </View>
        </SlideIn>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.background,
  },
  header: {
    paddingHorizontal: 20,
    paddingTop: 20,
    paddingBottom: 16,
  },
  greeting: {
    fontSize: 24,
    fontWeight: '700',
    color: colors.text,
    marginBottom: 4,
  },
  subGreeting: {
    fontSize: 16,
    color: colors.textSecondary,
  },
  section: {
    marginBottom: 24,
    paddingHorizontal: 20,
  },
  sectionHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 16,
  },
  iconWrapper: {
    marginRight: 8,
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: '600',
    color: colors.text,
    marginLeft: 8,
  },
  categoriesContainer: {
    paddingVertical: 16,
  },
  loadingContainer: {
    padding: 20,
    alignItems: 'center',
  },
  loadingText: {
    color: colors.textSecondary,
  },
  authPromptContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  welcomeGradient: {
    width: '100%',
    padding: 24,
    borderRadius: 16,
    marginBottom: 24,
    alignItems: 'center',
  },
  welcomeTitle: {
    fontSize: 28,
    fontWeight: '700',
    color: '#FFFFFF',
    marginBottom: 8,
  },
  welcomeSubtitle: {
    fontSize: 16,
    color: 'rgba(255, 255, 255, 0.9)',
  },
  authPromptTitle: {
    fontSize: 24,
    fontWeight: '700',
    color: colors.text,
    marginBottom: 12,
    textAlign: 'center',
  },
  authPromptText: {
    fontSize: 16,
    color: colors.textSecondary,
    textAlign: 'center',
    marginBottom: 24,
  },
  authButtonsContainer: {
    width: '100%',
    maxWidth: 300,
  }
});
