import React, { useState } from 'react';
import { View, Text, StyleSheet, FlatList, TouchableOpacity } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { useRouter } from 'expo-router';
import { Check } from 'lucide-react-native';
import { colors } from '@/constants/colors';
import { Button } from '@/components/Button';
import { useAuthStore } from '@/store/auth-store';
import { mockCategories } from '@/mocks/categories';
import { mockClasses } from '@/mocks/classes';

export default function OnboardingScreen() {
  const router = useRouter();
  const { setInterests, setSubjects } = useAuthStore();

  const [step, setStep] = useState(1);
  const [selectedClasses, setSelectedClasses] = useState<string[]>([]);
  const [selectedSubjects, setSelectedSubjects] = useState<string[]>([]);

  const handleClassToggle = (classId: string) => {
    setSelectedClasses(prev => 
      prev.includes(classId)
        ? prev.filter(id => id !== classId)
        : [...prev, classId]
    );
  };

  const handleSubjectToggle = (subjectId: string) => {
    setSelectedSubjects(prev => 
      prev.includes(subjectId)
        ? prev.filter(id => id !== subjectId)
        : [...prev, subjectId]
    );
  };

  const handleNext = () => {
    if (step === 1) {
      setStep(2);
    } else {
      // Save preferences
      setInterests(selectedClasses);
      setSubjects(selectedSubjects);

      // Navigate to home safely
      setTimeout(() => {
        router.replace('/');
      }, 0);
    }
  };

  const renderClassItem = ({ item }: { item: { id: string; name: string } }) => (
    <TouchableOpacity
      style={[
        styles.selectionItem,
        selectedClasses.includes(item.id) && styles.selectedItem
      ]}
      onPress={() => handleClassToggle(item.id)}
    >
      <Text 
        style={[
          styles.selectionItemText,
          selectedClasses.includes(item.id) && styles.selectedItemText
        ]}
      >
        {item.name}
      </Text>

      {selectedClasses.includes(item.id) && (
        <View style={styles.iconWrapper}>
          <Check size={16} color="#FFFFFF" />
        </View>
      )}
    </TouchableOpacity>
  );

  const renderSubjectItem = ({ item }: { item: { id: string; name: string; color: string } }) => (
    <TouchableOpacity
      style={[
        styles.selectionItem,
        selectedSubjects.includes(item.id) && { backgroundColor: item.color }
      ]}
      onPress={() => handleSubjectToggle(item.id)}
    >
      <Text 
        style={[
          styles.selectionItemText,
          selectedSubjects.includes(item.id) && styles.selectedItemText
        ]}
      >
        {item.name}
      </Text>

      {selectedSubjects.includes(item.id) && (
        <View style={styles.iconWrapper}>
          <Check size={16} color="#FFFFFF" />
        </View>
      )}
    </TouchableOpacity>
  );

  return (
    <SafeAreaView style={styles.container} edges={['top', 'bottom']}>
      <View style={styles.header}>
        <Text style={styles.stepText}>Step {step} of 2</Text>
        <Text style={styles.title}>
          {step === 1 
            ? "What are you preparing for?" 
            : "Select subjects you're interested in"}
        </Text>
        <Text style={styles.subtitle}>
          {step === 1 
            ? "Choose the exams or classes you're currently studying for" 
            : "We'll personalize your feed based on your interests"}
        </Text>
      </View>

      <View style={styles.selectionContainer}>
        {step === 1 ? (
          <FlatList
            data={mockClasses}
            renderItem={renderClassItem}
            keyExtractor={(item) => item.id}
            numColumns={2}
            contentContainerStyle={styles.selectionGrid}
          />
        ) : (
          <FlatList
            data={mockCategories}
            renderItem={renderSubjectItem}
            keyExtractor={(item) => item.id}
            numColumns={2}
            contentContainerStyle={styles.selectionGrid}
          />
        )}
      </View>

      <View style={styles.footer}>
        <Button
          title={step === 1 ? "Next" : "Get Started"}
          onPress={handleNext}
          disabled={step === 1 ? selectedClasses.length === 0 : selectedSubjects.length === 0}
          style={styles.button}
        />

        {step === 2 && (
          <TouchableOpacity onPress={() => setStep(1)}>
            <Text style={styles.backText}>Go Back</Text>
          </TouchableOpacity>
        )}
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.background,
    padding: 20,
  },
  header: {
    marginBottom: 32,
  },
  stepText: {
    fontSize: 14,
    color: colors.primary,
    fontWeight: '500',
    marginBottom: 8,
  },
  title: {
    fontSize: 24,
    fontWeight: '700',
    color: colors.text,
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 16,
    color: colors.textSecondary,
  },
  selectionContainer: {
    flex: 1,
  },
  selectionGrid: {
    paddingBottom: 20,
  },
  selectionItem: {
    flex: 1,
    margin: 8,
    padding: 16,
    borderRadius: 12,
    backgroundColor: colors.card,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  selectedItem: {
    backgroundColor: colors.primary,
  },
  selectionItemText: {
    fontSize: 14,
    fontWeight: '500',
    color: colors.text,
  },
  selectedItemText: {
    color: '#FFFFFF',
  },
  footer: {
    marginTop: 'auto',
    alignItems: 'center',
  },
  button: {
    width: '100%',
    marginBottom: 16,
  },
  backText: {
    color: colors.primary,
    fontSize: 16,
    fontWeight: '500',
  },
  iconWrapper: {
    // Wrapper to ensure icons are properly contained
  }
});
