import React from 'react';
import { LinearGradient } from 'expo-linear-gradient';
import { StyleSheet, ViewStyle } from 'react-native';
import { colors } from '@/constants/colors';

interface GradientProps {
  children?: React.ReactNode;
  style?: ViewStyle;
  variant?: 'primary' | 'secondary' | 'accent' | 'success' | 'info';
  colors?: [string, string];
  start?: { x: number; y: number };
  end?: { x: number; y: number };
}

export function Gradient({ 
  children, 
  style, 
  variant = 'primary', 
  colors: customColors,
  start = { x: 0, y: 0 },
  end = { x: 1, y: 1 }
}: GradientProps) {
  const getGradientColors = (): [string, string] => {
    if (customColors && customColors.length === 2) return customColors;

    switch (variant) {
      case 'primary':
        return [colors.primary, '#818CF8'];
      case 'secondary':
        return [colors.secondary, '#F472B6'];
      case 'accent':
        return [colors.accent, '#A78BFA'];
      case 'success':
        return [colors.success, '#34D399'];
      case 'info':
        return [colors.info, '#60A5FA'];
      default:
        return [colors.primary, '#818CF8'];
    }
  };

  return (
    <LinearGradient
      colors={getGradientColors()}
      start={start}
      end={end}
      style={[styles.gradient, style]}
    >
      {children}
    </LinearGradient>
  );
}

const styles = StyleSheet.create({
  gradient: {
    flex: 1,
  },
});
