import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, Image, ScrollView, TouchableOpacity, ActivityIndicator, Animated, Platform } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { useLocalSearchParams, Stack, useRouter } from 'expo-router';
import { Heart, Download, MessageSquare, Bookmark, BookmarkCheck, Share2 } from 'lucide-react-native';
import { colors } from '@/constants/colors';
import { Button } from '@/components/Button';
import { AdModal } from '@/components/AdModal';
import { useNotesStore } from '@/store/notes-store';
import { Note } from '@/types';
import { FadeIn } from '@/components/animations/FadeIn';
import { SlideIn } from '@/components/animations/SlideIn';
import { Gradient } from '@/components/ui/Gradient';

export default function NoteDetailScreen() {
  const { id } = useLocalSearchParams<{ id: string }>();
  const router = useRouter();
  const { notes, toggleBookmark, toggleLike } = useNotesStore();

  const [note, setNote] = useState<Note | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [showAdModal, setShowAdModal] = useState(false);
  const [isDownloading, setIsDownloading] = useState(false);

  // Animation values
  const scrollY = new Animated.Value(0);
  const headerOpacity = scrollY.interpolate({
    inputRange: [0, 100, 200],
    outputRange: [0, 0.5, 1],
    extrapolate: 'clamp',
  });

  const imageScale = scrollY.interpolate({
    inputRange: [-100, 0, 100],
    outputRange: [1.2, 1, 1],
    extrapolate: 'clamp',
  });

  const imageTranslateY = scrollY.interpolate({
    inputRange: [-100, 0, 100],
    outputRange: [0, 0, 50],
    extrapolate: 'clamp',
  });

  useEffect(() => {
    if (id) {
      const foundNote = notes.find(n => n.id === id);
      setNote(foundNote || null);
      setIsLoading(false);
    }
  }, [id, notes]);

  const handleDownload = () => {
    setShowAdModal(true);
  };

  const handleAdComplete = async () => {
    setIsDownloading(true);

    // Simulate download
    await new Promise(resolve => setTimeout(resolve, 1500));

    setIsDownloading(false);

    // Update download count (would be handled by the store in a real app)
    if (note) {
      setNote({
        ...note,
        downloads: note.downloads + 1
      });
    }
  };

  const handleBookmark = () => {
    if (note) {
      toggleBookmark(note.id);
    }
  };

  const handleLike = () => {
    if (note) {
      toggleLike(note.id);
    }
  };

  if (isLoading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color={colors.primary} />
        <Text style={styles.loadingText}>Loading note details...</Text>
      </View>
    );
  }

  if (!note) {
    return (
      <View style={styles.errorContainer}>
        <Text style={styles.errorTitle}>Note not found</Text>
        <Text style={styles.errorText}>The note you're looking for doesn't exist or has been removed.</Text>
        <Button
          title="Go Back"
          onPress={() => router.back()}
          variant="gradient"
          style={styles.errorButton}
        />
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container} edges={['bottom']}>
      <Stack.Screen 
        options={{
          title: note.subject,
          headerRight: () => (
            <TouchableOpacity style={styles.headerButton}>
              <View style={styles.iconWrapper}>
                <Share2 size={20} color={colors.text} />
              </View>
            </TouchableOpacity>
          ),
        }} 
      />

      <Animated.View 
        style={[
          styles.headerBackground, 
          { opacity: headerOpacity }
        ]}
      />

      <Animated.ScrollView 
        showsVerticalScrollIndicator={false}
        onScroll={Animated.event(
          [{ nativeEvent: { contentOffset: { y: scrollY } } }],
          { useNativeDriver: Platform.OS !== 'web' }
        )}
        scrollEventThrottle={16}
      >
        <Animated.View
          style={{
            transform: [
              { scale: imageScale },
              { translateY: imageTranslateY }
            ]
          }}
        >
          <Image 
            source={{ uri: note.thumbnailUrl }} 
            style={styles.thumbnail} 
          />
        </Animated.View>

        <FadeIn duration={600}>
          <View style={styles.content}>
            <Text style={styles.title}>{note.title}</Text>

            <View style={styles.metaContainer}>
              <Gradient 
                style={styles.badge}
                colors={[colors.primary, '#818CF8']}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 0 }}
              >
                <Text style={styles.badgeText}>{note.subject}</Text>
              </Gradient>

              <Gradient 
                style={styles.badge}
                colors={[colors.secondary, '#F472B6']}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 0 }}
              >
                <Text style={styles.badgeText}>{note.class}</Text>
              </Gradient>

              {note.board && (
                <View style={[styles.badge, { backgroundColor: '#F0F0F0' }]}>
                  <Text style={[styles.badgeText, { color: colors.textSecondary }]}>{note.board}</Text>
                </View>
              )}
            </View>

            <SlideIn direction="right" duration={600} delay={200}>
              <View style={styles.uploaderContainer}>
                <Image 
                  source={{ uri: note.uploaderAvatar || 'https://images.unsplash.com/photo-1599566150163-29194dcaad36' }} 
                  style={styles.avatar} 
                />
                <View>
                  <Text style={styles.uploaderName}>{note.uploaderName}</Text>
                  <Text style={styles.uploadDate}>
                    Uploaded on {new Date(note.createdAt).toLocaleDateString()}
                  </Text>
                </View>
              </View>
            </SlideIn>

            <View style={styles.divider} />

            <SlideIn direction="left" duration={600} delay={300}>
              <Text style={styles.sectionTitle}>Description</Text>
              <Text style={styles.description}>{note.description}</Text>
            </SlideIn>

            <View style={styles.divider} />

            <SlideIn direction="right" duration={600} delay={400}>
              <Text style={styles.sectionTitle}>Topics Covered</Text>
              <Text style={styles.topics}>{note.topic}</Text>
            </SlideIn>

            <FadeIn duration={600} delay={500}>
              <View style={styles.statsContainer}>
                <TouchableOpacity style={styles.statItem} onPress={handleLike}>
                  <View style={styles.iconWrapper}>
                    <Heart 
                      size={20} 
                      color={note.isLiked ? colors.error : colors.textTertiary} 
                      fill={note.isLiked ? colors.error : 'transparent'} 
                    />
                  </View>
                  <Text style={styles.statText}>{note.likes}</Text>
                </TouchableOpacity>

                <View style={styles.statItem}>
                  <View style={styles.iconWrapper}>
                    <Download size={20} color={colors.textTertiary} />
                  </View>
                  <Text style={styles.statText}>{note.downloads}</Text>
                </View>

                <View style={styles.statItem}>
                  <View style={styles.iconWrapper}>
                    <MessageSquare size={20} color={colors.textTertiary} />
                  </View>
                  <Text style={styles.statText}>{note.comments}</Text>
                </View>

                <TouchableOpacity style={styles.statItem} onPress={handleBookmark}>
                  <View style={styles.iconWrapper}>
                    {note.isBookmarked ? (
                      <BookmarkCheck size={20} color={colors.primary} />
                    ) : (
                      <Bookmark size={20} color={colors.textTertiary} />
                    )}
                  </View>
                </TouchableOpacity>
              </View>
            </FadeIn>
          </View>
        </FadeIn>
      </Animated.ScrollView>

      <FadeIn duration={600} delay={600}>
        <View style={styles.footer}>
          <Button
            title={isDownloading ? "Downloading..." : "Download Note"}
            onPress={handleDownload}
            isLoading={isDownloading}
            variant="gradient"
            icon={<View style={styles.iconWrapper}><Download size={20} color="#FFFFFF" /></View>}
            style={styles.downloadButton}
          />
        </View>
      </FadeIn>

      <AdModal 
        visible={showAdModal}
        onClose={() => setShowAdModal(false)}
        onAdComplete={handleAdComplete}
        noteId={note.id}
        noteName={note.title}
      />
    </SafeAreaView>
  );
}

