import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, Image, ScrollView, TouchableOpacity, RefreshControl, Alert, Animated, Platform } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { useRouter } from 'expo-router';
import { LogOut, Upload, DollarSign, Award, Users, Settings, ChevronRight, User, RefreshCw } from 'lucide-react-native';
import { colors } from '@/constants/colors';
import { Button } from '@/components/Button';
import { NoteCard } from '@/components/NoteCard';
import { EmptyState } from '@/components/EmptyState';
import { useAuthStore } from '@/store/auth-store';
import { useNotesStore } from '@/store/notes-store';
import { useEarningsStore } from '@/store/earnings-store';
import { FadeIn } from '@/components/animations/FadeIn';
import { SlideIn } from '@/components/animations/SlideIn';
import { Stagger } from '@/components/animations/Stagger';
import { Gradient } from '@/components/ui/Gradient';

export default function ProfileScreen() {
  const router = useRouter();
  const { user, isAuthenticated, logout } = useAuthStore();
  const { uploadedNotes, fetchUploadedNotes } = useNotesStore();
  const { earnings, fetchEarnings } = useEarningsStore();

  const [refreshing, setRefreshing] = useState(false);

  // Animation values
  const animatedScale = new Animated.Value(1);

  useEffect(() => {
    // Only load data if authenticated
    if (isAuthenticated && user) {
      loadData();
    }
  }, [isAuthenticated, user]);

  const loadData = async () => {
    if (user) {
      await fetchUploadedNotes(user.id);
      await fetchEarnings();
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadData();
    setRefreshing(false);
  };

  const handleLogout = () => {
    logout();
    // No need to navigate - the tab navigator will handle this
  };

  const handleManualRefresh = () => {
    // Animate the refresh button
    Animated.sequence([
      Animated.timing(animatedScale, {
        toValue: 0.8,
        duration: 150,
        useNativeDriver: Platform.OS !== 'web',
      }),
      Animated.timing(animatedScale, {
        toValue: 1,
        duration: 150,
        useNativeDriver: Platform.OS !== 'web',
      }),
    ]).start();

    Alert.alert(
      "Refresh Data",
      "Do you want to refresh your profile data?",
      [
        {
          text: "Cancel",
          style: "cancel"
        },
        {
          text: "Refresh",
          onPress: loadData
        }
      ]
    );
  };

  // If not authenticated, show login prompt instead of redirecting
  if (!isAuthenticated || !user) {
    return (
      <SafeAreaView style={styles.container} edges={['bottom']}>
        <View style={styles.authPromptContainer}>
          <EmptyState
            title="Your Profile"
            description="Log in to access your profile, uploads, and earnings"
            icon={<View style={styles.iconWrapper}><User size={40} color={colors.textTertiary} /></View>}
            actionLabel="Log In"
            onAction={() => router.push('/(auth)/login')}
            style={styles.emptyState}
          />
        </View>
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={styles.container} edges={['bottom']}>
      <ScrollView
        showsVerticalScrollIndicator={false}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        <FadeIn duration={600}>
          <View style={styles.header}>
            <View style={styles.profileInfo}>
              <Gradient 
                style={styles.avatarContainer}
                colors={[colors.primary, colors.accent]}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
              >
                <Image 
                  source={{ uri: user.avatar || 'https://images.unsplash.com/photo-1599566150163-29194dcaad36' }} 
                  style={styles.avatar} 
                />
              </Gradient>
              <View style={styles.nameContainer}>
                <Text style={styles.name}>{user.name}</Text>
                <Text style={styles.email}>{user.email}</Text>
              </View>
            </View>

            <View style={styles.headerButtons}>
              <Animated.View style={{ transform: [{ scale: animatedScale }] }}>
                <TouchableOpacity 
                  style={styles.refreshButton}
                  onPress={handleManualRefresh}
                >
                  <View style={styles.iconWrapper}>
                    <RefreshCw size={20} color={colors.primary} />
                  </View>
                </TouchableOpacity>
              </Animated.View>

              <Button
                title="Log Out"
                onPress={handleLogout}
                variant="outline"
                size="small"
                icon={<View style={styles.iconWrapper}><LogOut size={16} color={colors.primary} /></View>}
              />
            </View>
          </View>
        </FadeIn>

        <SlideIn direction="right" duration={600} delay={200}>
          <View style={styles.statsContainer}>
            <View style={styles.statItem}>
              <Text style={styles.statValue}>{user.level}</Text>
              <Text style={styles.statLabel}>Level</Text>
            </View>

            <View style={styles.statDivider} />

            <View style={styles.statItem}>
              <Text style={styles.statValue}>{user.followers}</Text>
              <Text style={styles.statLabel}>Followers</Text>
            </View>

            <View style={styles.statDivider} />

            <View style={styles.statItem}>
              <Text style={styles.statValue}>{user.following}</Text>
              <Text style={styles.statLabel}>Following</Text>
            </View>
          </View>
        </SlideIn>

        <SlideIn direction="left" duration={600} delay={300}>
          <View style={styles.menuContainer}>
            <TouchableOpacity 
              style={styles.menuItem}
              onPress={() => router.push('/upload')}
            >
              <Gradient 
                style={styles.menuIconContainer}
                colors={[colors.primary, '#818CF8']}
              >
                <View style={styles.iconWrapper}>
                  <Upload size={20} color="#FFFFFF" />
                </View>
              </Gradient>
              <Text style={styles.menuText}>Upload New Note</Text>
              <View style={styles.iconWrapper}>
                <ChevronRight size={20} color={colors.textTertiary} />
              </View>
            </TouchableOpacity>

            <TouchableOpacity 
              style={styles.menuItem}
              onPress={() => router.push('/earnings')}
            >
              <Gradient 
                style={styles.menuIconContainer}
                colors={[colors.success, '#34D399']}
              >
                <View style={styles.iconWrapper}>
                  <DollarSign size={20} color="#FFFFFF" />
                </View>
              </Gradient>
              <View style={styles.menuTextContainer}>
                <Text style={styles.menuText}>Earnings</Text>
                <Text style={styles.menuSubtext}>₹{earnings.withdrawable.toFixed(2)} available</Text>
              </View>
              <View style={styles.iconWrapper}>
                <ChevronRight size={20} color={colors.textTertiary} />
              </View>
            </TouchableOpacity>

            <TouchableOpacity style={styles.menuItem}>
              <Gradient 
                style={styles.menuIconContainer}
                colors={[colors.warning, '#FBBF24']}
              >
                <View style={styles.iconWrapper}>
                  <Award size={20} color="#FFFFFF" />
                </View>
              </Gradient>
              <View style={styles.menuTextContainer}>
                <Text style={styles.menuText}>XP & Rewards</Text>
                <Text style={styles.menuSubtext}>{user.xp} XP earned</Text>
              </View>
              <View style={styles.iconWrapper}>
                <ChevronRight size={20} color={colors.textTertiary} />
              </View>
            </TouchableOpacity>

            <TouchableOpacity style={styles.menuItem}>
              <Gradient 
                style={styles.menuIconContainer}
                colors={[colors.info, '#60A5FA']}
              >
                <View style={styles.iconWrapper}>
                  <Users size={20} color="#FFFFFF" />
                </View>
              </Gradient>
              <Text style={styles.menuText}>Followers & Following</Text>
              <View style={styles.iconWrapper}>
                <ChevronRight size={20} color={colors.textTertiary} />
              </View>
            </TouchableOpacity>

            <TouchableOpacity style={styles.menuItem}>
              <View style={[styles.menuIconContainer, { backgroundColor: colors.textTertiary }]}>
                <View style={styles.iconWrapper}>
                  <Settings size={20} color="#FFFFFF" />
                </View>
              </View>
              <Text style={styles.menuText}>Settings</Text>
              <View style={styles.iconWrapper}>
                <ChevronRight size={20} color={colors.textTertiary} />
              </View>
            </TouchableOpacity>
          </View>
        </SlideIn>

        <FadeIn duration={600} delay={400}>
          <View style={styles.uploadedNotesContainer}>
            <View style={styles.sectionHeader}>
              <Text style={styles.sectionTitle}>Your Uploaded Notes</Text>
              <Text style={styles.noteCount}>{uploadedNotes.length} notes</Text>
            </View>

            {uploadedNotes.length === 0 ? (
              <EmptyState
                title="No uploads yet"
                description="Share your knowledge by uploading study notes"
                icon={<View style={styles.iconWrapper}><Upload size={40} color={colors.textTertiary} /></View>}
                actionLabel="Upload Note"
                onAction={() => router.push('/upload')}
              />
            ) : (
              <Stagger staggerDelay={150}>
                {uploadedNotes.map((note, index) => (
                  <NoteCard key={note.id} note={note} delay={index * 100} />
                ))}
              </Stagger>
            )}
          </View>
        </FadeIn>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.background,
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 20,
    paddingTop: 20,
    paddingBottom: 16,
  },
  profileInfo: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  avatarContainer: {
    width: 64,
    height: 64,
    borderRadius: 32,
    padding: 2,
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 16,
  },
  avatar: {
    width: 60,
